func transformDownloadTaskStateToArtworksviewsDownloadTaskStateView(v *DownloadTaskState) *artworksviews.DownloadTaskStateView {
	if v == nil {
		return nil
	}
	res := &artworksviews.DownloadTaskStateView{
		Date:   &v.Date,
		Status: &v.Status,
	}

	return res
}
